(249, 1, '2021-04-14 22:01:30', '2021-04-14 22:01:30', '[et_pb_section fb_built=\"1\" fullwidth=\"on\" _builder_version=\"4.9.3\" _module_preset=\"default\" background_enable_image=\"off\"][et_pb_fullwidth_header title=\"Welcome to Houston Kidney Specialists Center\" subhead=\"Houston Kidney Specialists Center is based in the Northwest Houston area. The practice is focused on caring for patients with kidney diseases including Acute Kidney Injury or Failure, Chronic Kidney Disease, Hemodialysis, Peritoneal Dialysis, HOME Dialysis, Post Kidney Transplantation Care , Cystic Kidney diseases, Glomerulonephritis , Diabetic kidney Diseases, Kidney Stones, Urine Abnormalities and Hypertension Management. \" header_image_url=\"http://localhost/kidneyCenter/wp-content/uploads/2021/04/kc_doctor.png\" _builder_version=\"4.9.3\" _module_preset=\"default\" title_font=\"Montserrat|700|||||||\" title_text_color=\"#182422\" title_font_size=\"46.03px\" subhead_font=\"Montserrat|600|||||||\" subhead_text_color=\"#182422\" subhead_font_size=\"20px\" background_enable_color=\"off\" background_image=\"http://localhost/kidneyCenter/wp-content/uploads/2021/04/kc_header.png\" height=\"788px\" animation_style=\"fade\" border_color_all_image=\"RGBA(0,0,0,0)\" border_style_all_image=\"none\"][/et_pb_fullwidth_header][/et_pb_section][et_pb_section fb_built=\"1\" _builder_version=\"4.9.3\" _module_preset=\"default\"][et_pb_row column_structure=\"1_2,1_2\" _builder_version=\"4.9.3\" _module_preset=\"default\" width=\"75%\" custom_margin=\"|20px||20px|false|true\"][et_pb_column type=\"1_2\" _builder_version=\"4.9.3\" _module_preset=\"default\"][et_pb_image src=\"http://localhost/kidneyCenter/wp-content/uploads/2021/04/section2.png\" alt=\"section2\" title_text=\"section2\" _builder_version=\"4.9.3\" _module_preset=\"default\" width=\"525px\" height=\"555px\" custom_margin=\"|20px||20px|false|true\"][/et_pb_image][/et_pb_column][et_pb_column type=\"1_2\" _builder_version=\"4.9.3\" _module_preset=\"